import React, { useEffect, useState } from 'react';
import {fetchData} from './api/api';

const Heading = ({ content }) => {
     const styles = {
          textAlign: 'center'
     }

     return (
          <h1 style={ styles } id='scrambled-word'>{ content }</h1>
     )
}

const App = () => {
     const [data, setData] = useState({});
     const [sentence, setSentence] = useState('');

     useEffect(() => {
          const getData = async () => {
               const response = await fetchData(1);
               const { data } = response.data;
               setData(data);
               setSentence(scrambledWord(data));
          };

          getData().catch(err => console.log(err.message));
     },[]);

     useEffect(() => {
          if(data) setSentence(scrambledWord);
     }, [scrambledWord()])

     const scrambledWord = () => {
          const sentenceArr = data.sentence.split(' ');
          let newSentenceArr = [];

          sentenceArr.forEach(word => {
               if (word.length <= 3) {
                    newSentenceArr.push(word)
               } else {
                    const oldWord = word.split('').slice(1, -1);
                    const newWord = (word[0] + word[word.length - 1]).split('');

                    for(let i = 0; i < oldWord.length; i++){
                         if(oldWord.length === 2){
                              const tmp = oldWord[0]
                              oldWord[0] = oldWord[1]
                              oldWord[1] = tmp;
                         } 
                         const a = Math.floor(Math.random() * oldWord.length);
                         let tmp = oldWord[i];
                         oldWord[i] = oldWord[a];
                         oldWord[a] = tmp
                    }
                    oldWord.map(i => newWord.splice(2, 0, i));
                    newSentenceArr.push(newWord.join(''));
               }
          });

          return newSentenceArr.join(' ');
     };



     return (
          <>
               <Heading content={ sentence }/>
               <p onClick={ scrambledWord }>CLick</p>
          </>
     );
};

export default App;
