import React, { useEffect, useState } from 'react';
import {fetchData} from './api/api';
import { scrambledWord } from './helpers/ScrambleWord';
import './App.css';

const Heading = ({ content }) => {
     const styles = {
          textAlign: 'center'
     }

     return (
          <h1 style={ styles } id='scrambled-word'>{ content }</h1>
     )
}

const App = () => {
     const [data, setData] = useState({sentence: ''});
     const [sentence, setSentence] = useState('');
     const [sentenceToCompare, setSentenceToCompare] = useState({});
     let letterId;
     console.log(sentenceToCompare);

     useEffect(() => {
          const getData = async () => {
               const response = await fetchData(9);
               const { data } = await response.data;
               setData(data);
          };

          getData().catch(err => console.log(err.message));
     },[]);

     useEffect(() => {
          if (data) setSentence(scrambledWord(data))
     },[data]);

     const handleLettersInput = (e) => {
          e.preventDefault();
          setSentenceToCompare({...sentenceToCompare, [e.target.name]: {value: e.target.value, correctGuess: false}})

          // const itemToCompare = sentenceToCompare[e.target.id];
          const arr = Array.from(data.sentence);

          arr.map((item, index) => {
               if(index === Number(e.target.id) && item === e.target.value){
                    console.log('true');
                    console.log(sentenceToCompare);
               } else {
                    return false
               }
          })
     };

     const nextItemChange = (e) => {
          if(e.keyCode === 46 || e.keyCode === 8){
               const item = document.getElementById(`${Number(e.target.id) - 1}`);
               if(!item) {
                    return false
               } 
               item.focus();
          } else {
               const item = document.getElementById(`${Number(e.target.id) + 1}`);
               if(!item) {
                    return false
               } 
               item.focus()
          }
     };

     return (
          <main id='main'>
               <div className='container'>
                    <Heading content={ sentence }/>
                    <p className='par'>Guess the sentence! Starting typing</p>
                    <p className='par'>The yellow blocks are meant for spaces</p>
                    <p className='par'>Score: 0</p>
                    <form>
                         {data.sentence.split(/(\s+)/).map((word, wordIndex) => {

                              return (
                                   <div key={ wordIndex + 100 }>
                                        { word.split('').map((letter, index) => {
                                             if(index === 0 && wordIndex === 0){
                                                  letterId = 0;
                                             } else {
                                                  letterId += 1;
                                             }
                                             if(letter === ' ' ){
                                                  return (
                                                       <input 
                                                       key={index} 
                                                       id={letterId}
                                                       name={letterId}
                                                       value={ sentenceToCompare[letter] } 
                                                       onChange={ handleLettersInput } 
                                                       maxLength={1} 
                                                       onKeyUp={ nextItemChange }
                                                       className={`${ sentenceToCompare[letterId]?.correctGuess ? 'correct' : 'space' }`}
                                                       />
                                                  )
                                             } else {
                                                  return (
                                                       <input 
                                                            key={index} 
                                                            id={letterId}
                                                            name={letterId}
                                                            value={ sentenceToCompare[letter] } 
                                                            onChange={ handleLettersInput } 
                                                            maxLength={1} 
                                                            onKeyUp={ nextItemChange }
                                                            className={`${ sentenceToCompare[letterId]?.correctGuess ? 'correct' : 'letter' }`}
                                                            />
                                                  ) 
                                             }
                                        })}
                                   </div>
                              )
                         })}
                    </form>
                    <div>
                         <button>Click</button>
                    </div>
               </div>
          </main>
     );
};

export default App;
