import React, { useEffect, useState } from 'react';
import {fetchData} from './api/api';
import { scrambledWord } from './helpers/ScrambleWord';
import './App.css';

const Heading = ({ content }) => {
     const styles = {
          textAlign: 'center'
     }

     return (
          <h1 style={ styles } id='scrambled-word'>{ content }</h1>
     )
}

const App = () => {
     const [data, setData] = useState({sentence: ''});
     const [sentenceToCompare, setSentenceToCompare] = useState({});

          const getData = async () => {
               const response = await fetchData(1);
               const { data } = await response.data;
               console.log(data)
               setData(data);
          };

          getData().catch(err => console.log(err.message))


     const handleLettersInput = (e) => {
          e.preventDefault();
          setSentenceToCompare({...sentenceToCompare, [e.target.id]: e.target.value})
     }

     return (
          <main id='main'>
               <Heading content={ scrambledWord(data) }/>
               <p className='par'>Guess the sentence! Starting typing</p>
               <p className='par'>The yellow blocks are meant for spaces</p>
               <form>
                    {  data.sentence.split('').map((letter, index) => {
                         return <input key={index} id={index} value={ sentenceToCompare[letter] } onChange={ handleLettersInput } />
                    }) }
               </form>
          </main>
     );
};

export default App;
