import React, { useEffect, useState } from 'react';
import {fetchData} from './api/api';

const Heading = ({ content }) => {
     const styles = {
          textAlign: 'center'
     }

     return (
          <h1 style={ styles } id='scrambled-word'>{ content }</h1>
     )
}

const App = () => {
     const [data, setData] = useState('');

     useEffect(() => {
          const getData = async () => {
               const response = await fetchData(1);
               const { data } = response.data;
               setData(data);
          };

          getData().catch(err => console.log(err.message));
     },[]);

     const scrambledWord = () => {
          const sentenceArr = data.sentence.split(' ');
          let newSentenceArr = [];

          sentenceArr.forEach(word => {
               if (word.length < 2) {
                    newSentenceArr.push(word)
               } else {
                    const newWord = word.split('');
                    for(let i = 0; i < newWord.length; i++){
                         const a = Math.floor(Math.random() * newWord.length);
                         let tmp = newWord[i];
                         if(newWord[a] === 0 || newWord[a] === newWord.length - 1 ){
                              return newWord[i]
                         } else {
                              newWord[i] = newWord[a];
                              newWord[a] = tmp
                         }
                    }

                    newSentenceArr.push(newWord.join(''));
               }

          })

          console.log(newSentenceArr);
     };

     return (
          <>
               <Heading content={ data.sentence }/>
               <p onClick={ scrambledWord }>CLick</p>
          </>
     );
};

export default App;
