import React, { useEffect, useState } from 'react';
import {fetchData} from './api/api';
import { scrambledWord } from './helpers/ScrambleWord';
import './App.css';

const Heading = ({ content }) => {
     const styles = {
          textAlign: 'center'
     }

     return (
          <h1 style={ styles } id='scrambled-word'>{ content }</h1>
     )
}

const App = () => {
     const [data, setData] = useState(null);
     const [sentence, setSentence] = useState('');

     useEffect(() => {
          const getData = async () => {
               const response = await fetchData(1);
               const json = await response.json();
               setData(json.data);
          };

          getData()
     },[]);

     // useEffect(() => {
     //      if (data) {
     //           setSentence(scrambledWord(data));
     //      }
     // }, [data]);
     console.log(data);
     return (
          <main id='main'>
               {/* <Heading content={ sentence }/> */}
               <p className='par'>Guess the sentence! Starting typing</p>
               <p className='par'>The yellow blocks are meant for spaces</p>
               { data.sentence }
          </main>
     );
};

export default App;
