import React, { useEffect, useState } from 'react';
import {fetchData} from './api/api';
import { scrambledWord } from './helpers/ScrambleWord';
import './App.css';

const Heading = ({ content }) => {
     const styles = {
          textAlign: 'center'
     }

     return (
          <h1 style={ styles } id='scrambled-word'>{ content }</h1>
     )
}

const App = () => {
     const [data, setData] = useState();
     const [sentence, setSentence] = useState('');

     useEffect(() => {
          const getData = async () => {
               const response = await fetchData(1);
               const { data } = response.data;
               setData(data);
          };

          getData().catch(err => console.log(err.message));
     },[]);

     useEffect(() => {
          if (data) {
               setSentence(scrambledWord(data));
          }
     }, [data])

     return (
          <main className='main'>
               <Heading content={ sentence }/>
          </main>
     );
};

export default App;
